#OPENS FASTA FILE
def open_fasta():
    global search_button, radio_var  # Declare search_button and radio_var globally

    """Opens a FASTA file and displays its content in the text area."""
    filename = filedialog.askopenfilename(title="Open FASTA File", filetypes=[("FASTA/FNA files", "*.fasta;*.fna")])
    if filename:
        try:
            with open(filename, "r") as fasta_file:
                fasta_data = []
                for line in fasta_file:
                    line = line.strip()  # Remove leading/trailing whitespace
                    if not line.startswith('>'):  # Skip header lines
                        fasta_data.append(line)
                fasta_text.delete("1.0", tkinter.END)
                fasta_text.insert("1.0", ''.join(fasta_data))

            # Enable search button and create radio buttons
            search_button.state(['!disabled'])
            create_radio_buttons()

        except FileNotFoundError:
            print("File not found!")

# function for gyrA
def find_gyra_in_fasta(fasta_content, gyrA_sequences):
  """
  Checks if any of the gyrA sequences are present in the fasta content.

  Args:
      fasta_content: The content of the fasta file as a string.
      gyrA_sequences: A list of gyrA reference sequences.

  Returns:
      A list of gyrA sequences found in the fasta content. and if it is present
  """
  found_gyra = []
  for sequence in gyrA_sequences:
    if sequence in fasta_content:
      found_gyra.append(sequence)
  return found_gyra


def find_msrA1_in_fasta(fasta_content, msrA1_sequences):
  found_msrA1 = []
  for sequence1 in msrA1_sequences:
    if sequence1 in fasta_content:
      found_msrA1.append(sequence1)
  return found_msrA1

def find_msrA2_in_fasta(fasta_content, msrA2_sequences):
  found_msrA2 = []
  for sequence2 in msrA2_sequences:
    if sequence2 in fasta_content:
      found_msrA2.append(sequence2)
  return found_msrA2

def find_msrB_in_fasta(fasta_content, msrB_sequences):
  found_msrB = []
  for sequence3 in msrB_sequences:
    if sequence3 in fasta_content:
      found_msrB.append(sequence3)
  return found_msrB
# find the string in the window
def highlight_text(text_window, string_to_highlight, highlight_color="red"):
    """Highlights all occurrences of the given string in the text window.

    Args:
        text_window (tk.Text): The text window widget where highlighting will be applied.
        string_to_highlight (str): The string to be highlighted.
        highlight_color (str, optional): The color to use for highlighting. Defaults to "yellow".
    """

    start_index = "1.0"  # Start at the beginning of the text
    while True:
        # Find the next occurrence of the string (case-insensitive)
        index = text_window.search(string_to_highlight, start_index, nocase=True, stopindex=tkinter.END)
        if not index:  # No more occurrences found
            break

        # Get the end index of the string
        end_index = "{}+{}c".format(index, len(string_to_highlight))

        # Create a new tag for highlighting (if it doesn't exist)
        if "highlight" not in text_window.tag_names():
            text_window.tag_configure("highlight", foreground=highlight_color)

        # Apply the "highlight" tag to the found string
        text_window.tag_add("highlight", index, end_index)

        # Update the starting index for the next search to avoid infinite loop
        start_index = end_index
        
def create_radio_buttons():
    global radio_var  # Access the global variable

    # Frame to hold the radio buttons
    radio_frame = ttk.Frame(root)
    radio_frame.pack(padx=5, pady=5)

    radio_var = tkinter.StringVar()  # Create a StringVar for radio button selection

    # Define radio button labels and values
    radio_options = [
        ("gyrA", "gyrA"),
        ("msrA_1", "msrA_1"),
        ("msrA_2", "msrA_2"),
        ("msrB", "msrB"),
    ]

    for text, value in radio_options:
        radio_button = ttk.Radiobutton(
            radio_frame, text=text, variable=radio_var, value=value
        )
        radio_button.pack(anchor=tkinter.W)  # Pack radio buttons horizontally

    # Add functionality to the search button based on radio button selection
    def search_based_on_selection():
        # Get the FASTA data from the text widget and skip the first line; 
        fasta_data = fasta_text.get("1.0", tkinter.END)
        selected_option = radio_var.get()
        if selected_option == "gyrA":
          
          
          #FUNCTION FOR FINDING gyrA
          found_gyra_sequences = find_gyra_in_fasta(fasta_data, gyrA)
          
          if found_gyra_sequences:
              print("gyrA is found in your input file.")
              search_button_label.config(text="gyrA found.")
              # PRINTS THE FOUND SEQUENCE IN TERMINAL
            #   print("gyrA sequence found in the fasta file:")
              
            #   for sequence in found_gyra_sequences:
            #       print(sequence)
              
              # Define your string to highlight
              string_to_highlight = found_gyra_sequences[0]
              
              # Highlight the string in the text window
              highlight_text(fasta_text, string_to_highlight)
          else:
              print("No gyrA sequences found in the fasta file.")
              search_button_label.config(text="No gyrA sequence found.")
        if selected_option=="msrA_1":
          
          #FUNCTION FOR FINDING msrA1
          found_msrA1_sequences = find_msrA1_in_fasta(fasta_data, msrA_1)
          
          if found_msrA1_sequences:
              print("msrA_1 is found in your input file.")
              search_button_label.config(text="msrA_1 found.")
              # PRINTS THE FOUND SEQUENCE IN TERMINAL
            #   print("gyrA sequence found in the fasta file:")
              
            #   for sequence in found_gyra_sequences:
            #       print(sequence)
              
              # Define your string to highlight
              string_to_highlight = found_msrA1_sequences[0]
              
              # Highlight the string in the text window
              highlight_text(fasta_text, string_to_highlight)
          else:
                print("No msrA_1 sequences found in the fasta file.")
                search_button_label.config(text="No msrA_1 sequence found.")
        if selected_option=='msrA_2':
            #FUNCTION FOR FINDING msrA2
          found_msrA2_sequences = find_msrA2_in_fasta(fasta_data, msrA_2)
          
          if found_msrA2_sequences:
              print("msrA_2 is found in your input file.")
              search_button_label.config(text="msrA_2 found.")
             
              
              # Define your string to highlight
              string_to_highlight = found_msrA2_sequences[0]
              
              # Highlight the string in the text window
              highlight_text(fasta_text, string_to_highlight)
          else:
                print("No msrA_2 sequences found in the fasta file.")
                search_button_label.config(text="No msrA_2 sequence found.")
        if selected_option=='msrB':
            #FUNCTION FOR FINDING msrA2
          found_msrB_sequences = find_msrA2_in_fasta(fasta_data, msrB)
          
          if found_msrB_sequences:
              print("msrB is found in your input file.")
              search_button_label.config(text="msrB found.")
             
              
              # Define your string to highlight
              string_to_highlight = found_msrB_sequences[0]
              
              # Highlight the string in the text window
              highlight_text(fasta_text, string_to_highlight)
          else:
                print("No msrB sequences found in the fasta file.")
                search_button_label.config(text="No msrB sequence found.")
        
        # print(f"Search using option: {selected_option}")

    search_button.config(command=search_based_on_selection)


